import { BrowserRouter, Route, Link } from "react-router-dom"
import { useContext } from "react";
import { AuthContext } from "./context/AuthContext";
function App() {
  const { user } = useContext(AuthContext);
  return (
    <BrowserRouter>

      <Route exact path="/">
        {user ? <Home /> : <Register />}
      </Route>




      <Login />
    </BrowserRouter>
  );
}

export default App;
